const white = "#FFFFFF";
const black = "#222222";
const gray = "#F8F8F8";

const themeLight = {
  margin: 'none',
  background: gray,
  body: black
};

const themeDark = {
  margin: 'none',
  background: black,
  body: white
};

const theme = mode => (mode === "dark" ? themeDark : themeLight);

export default theme;
